import React, {useEffect,useState } from 'react'
import {loginStudent} from '../services/StudentService'

const LoginStudentComponent = () => {

    const [contactNumber, setContactNumber]=useState([])
    const [password, setPassword]=useState([])
    const [token, setToken]=useState([])


    function handleContactNumber(e){
        setContactNumber(e.target.value);
    }

    function handlePassword(e){
        setPassword(e.target.value);
    }


    function login(e){
        e.preventDefault();
        const student= {contactNumber,password}
        console.log(student);
        loginStudent(student).then((response) => {
            localStorage.setItem('token',response.data.jwt)
            console.log(localStorage.getItem('token'));
        })
    }

        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    <h3 className="text-center">Student Login</h3>
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Contact Number: </label>
                                            <input placeholder="Contact Number" name="contactNumber" className="form-control" 
                                                value={contactNumber} onChange={handleContactNumber}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Last Name: </label>
                                            <input type="password" placeholder="Password" name="password" className="form-control" 
                                                value={password} onChange={handlePassword}/>
                                        </div>
                                        <button className="btn btn-success" onClick={login}>Login</button>
                                        </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )



}

export default LoginStudentComponent
